import React, {Component}from 'react';
import {MapTo} from '@adobe/aem-react-editable-components';


export const CustomEditConfig = {
       emptyLabel: 'Header',
       isEmpty: function(props) {
         return !props || !props.logo;
       }
};

export default function Header(props){
 console.log('Received props:', props); // Log all props
     console.log('Logo:', props.logo);      // Log logo separately
     console.log('Link:', props.link);      // Log link separately

    if (CustomEditConfig.isEmpty(props)) {
        return null;
    }

     // Default to an empty array if props.link is undefined or not an array
        const links = Array.isArray(props.link) ? props.link : [];

    return (
        <div className="header">
        Hello
            <h4>{props.logo}</h4>
            <div className="DemoComponent__navbar">
                           {links.length > 0 ? (
                               links.map((item, index) => (
                                   <ul key={index}>
                                       <li><h4>{item.name}, {item.url}</h4></li>
                                   </ul>
                               ))
                           ) : (
                               <p>No links available</p>
                           )}
            </div>
        </div>
    );
};

