Header.propTypes = {
    logo: PropTypes.string,
    link: PropTypes.arrayOf(PropTypes.shape({
        name: PropTypes.string.isRequired,
        url: PropTypes.string.isRequired
    }))
};

Header.defaultProps = {
    link: []
};


MapTo('spa-react/components/content/header')(Header,CustomEditConfig);
