import React from 'react';
import { MapTo } from '@adobe/aem-react-editable-components';
import Header from '../Header/Header'; // Adjusted import path
require('./Banner.css');

export const CustomEditConfig = {
    emptyLabel: 'Banner',

    isEmpty: function (props) {
        return !props || !props.title;
    }
};

export default function Banner(props) {
    if (CustomEditConfig.isEmpty(props)) {
        return <div><h1>Empty</h1></div>;
    }

    // Determine styles based on the presence of bannerImg and bannerImg2
    const bannerStyle = {
        backgroundImage: props.bannerImg ? `linear-gradient(rgba(6, 10, 27, 0.74),rgba(6, 10, 27, 0.74)),url(${props.bannerImg})` : undefined,
        height: props.bannerImg2 ? "50vh" : "100vh",
        backgroundPosition: "center",
        backgroundSize: "cover",
        position: "relative",
    };

    const headerStyle = {
        backgroundImage: props.bannerImg2 ? `linear-gradient(rgba(4, 9, 30, 0.7), rgba(4, 9, 30, 0.7)),url(${props.bannerImg2})` : undefined,
        height: props.bannerImg2 ? "50vh" : "0",
        backgroundPosition: "center",
        backgroundSize: "cover",
    };

    return (
        <section>
            <Header logo={props.headerLogo} link={props.link} />
            <div className="text-box">
                <h1>{props.title}</h1>
                <div className="desc-box">
                    <p dangerouslySetInnerHTML={{ __html: props.bannerText }} />
                </div>
                <a href={`${props.btnLink}.html`} className="hero-btn">{props.btnTitle}</a>
            </div>

            <div className="sub-header" style={headerStyle}>
            </div>
            <div className="banner" style={bannerStyle}>
            </div>
        </section>
    );
}

MapTo('spa-react/components/content/baner')(Banner, CustomEditConfig);
