// Define your functional component
function Contact(props){
    if (CustomEditConfig.isEmpty(props)) {
        return null;
    }
    return (
        <div>
            <h4>{props.name}</h4>
            <p>{props.email}</p>
            <p>{props.description}</p>
        </div>
    );
};


MapTo('spa-react/components/contact')(Contact,CustomEditConfig);
export default Contact;
