import React from 'react';
import {MapTo} from '@adobe/aem-react-editable-components';
import Header from '../Header/Header'; // Adjusted import path
require('./Banner.css');

export const CustomEditConfig = {
       emptyLabel: 'Banner',

       isEmpty: function(props) {
         return !props || !props.title;
       }
};

export default function Banner(props){

    if (CustomEditConfig.isEmpty(props)) {

        return <div><h1>Empty</h1></div>;
    }

    const myStyle = {
            backgroundImage:`linear-gradient(rgba(6, 10, 27, 0.74),rgba(6, 10, 27, 0.74)),url(${props.bannerImg})`,
            height: "765px",

            backgroundPosition:"center",
            backgroundSize: "cover",
            position: "relative",
     };
     const headerStyle = {
            backgroundImage:`linear-gradient(rgba(4, 9, 30, 0.7), rgba(4, 9, 30, 0.7)),url(${props.bannerImg2})`
     }

    return (
    <div>
        <div className="sub-header" style={headerStyle}>
        </div>
    	<section className="banner" style={myStyle} >
                    <Header logo={props.headerLogo} link={props.link} />
                    <div className="text-box">
                        <h1>{props.title}</h1>
                        <div className="desc-box">
                        <p  dangerouslySetInnerHTML={{ __html: props.bannerText }}/>
                        </div>
                        <a href={props.btnLink+".html"} className="hero-btn">{props.btnTitle}</a>
                    </div>

    	</section>
     </div>
    );
};




MapTo('spa-react/components/content/baner')(Banner,CustomEditConfig);
