import React from 'react';
import { MapTo } from '@adobe/aem-react-editable-components';
import './Course.css';

export const CustomEditConfig = {
    emptyLabel: 'Course',
    isEmpty: function (props) {
        return !props || !props.course || props.course.length === 0;
    }
};

const Course = (props) => {
    // Log the props to debug
    console.log('Course Props:', props);

    // Default to an empty array if props.course is undefined or not an array
    const courseItems = Array.isArray(props.course) ? props.course : [];

    if (CustomEditConfig.isEmpty(props)) {
        return <div><h1>No Data Available</h1></div>;
    }

    return (
        <section className="course">
            <h1>{props.courseTitle}</h1>
            <p>{props.courseDescription}</p>

            <div className="row">
                {courseItems.map((item, index) => (
                    <div className="campus-col" key={index}>
                        <img src={item.courseImg} alt={`Course Item ${index}`} />
                        <div className="layer">
                            <h3>{item.courseText}</h3>
                        </div>
                    </div>
                ))}
            </div>
        </section>
    );
};

MapTo('spa-react/components/content/course')(Course, CustomEditConfig);

export default Course;
