import React from 'react';
import {MapTo} from '@adobe/aem-react-editable-components';
require('./BlogList.css')
export const CustomEditConfig = {
       emptyLabel: 'Blog List',
       isEmpty: function(props) {
          return !props || !props.blogList;
       }
};
export default function BlogList(props){
     const blogListItems = Array.isArray(props.blogList) ? props.blogList : [];

    if (CustomEditConfig.isEmpty(props)) {
        return <div><h1>Hello</h1></div>;
    }
    return (
        <section className="blog-tiles">
            {blogListItems.map((item, index) => (
                <div className="blog-tiles-list" key={index}>
                    <a href={item.blogLink} style={{ textDecoration: "none" }} className="blog-item">
                        <div
                            id="dispatcher"
                            className="blog-item-div"
                            style={{
                                border: "1px solid #ccc",
                                borderRadius: "8px",
                                overflow: "hidden",
                                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)"
                            }}
                        >
                            <img
                                src={item.blogImg}
                                alt="Card Image"
                                style={{ width: "100%", height: "auto", display: "block" }}
                            />
                            <div style={{ padding: "16px" }}>
                                <p
                                    className="dispatcherTag"
                                    style={{
                                        marginTop: "7px",
                                        marginBottom: "7px",
                                        fontSize: "13px",
                                        borderRadius: "20px",
                                        width: "fit-content",
                                        padding: "3px 15px"
                                    }}
                                >
                                    {item.blogName}
                                </p>
                                <div className="one-line">
                                    <p className="blog-title" style={{ fontSize: "1em", color: "#555" }}>
                                        {item.blogTitle}
                                    </p>
                                </div>
                                <p className="publishDate">{item.blogDate}</p>
                            </div>
                        </div>
                    </a>
                </div>
            ))}
        </section>
    );

};


MapTo('spa-react/components/content/blogList')(BlogList,CustomEditConfig);
