import React from 'react';
import {MapTo} from '@adobe/aem-react-editable-components';

export const CustomEditConfig = {
       emptyLabel: 'About',

       isEmpty: function(props) {
         return !props || !props.aboutImg;
       }
};

export default function About(props){

    if (CustomEditConfig.isEmpty(props)) {

        return null;
    }
    console.log('Rendering Banner component');
    return (
    	<section className="about-us" >
    	    <div className="flex">
    	       <div class="about-col home-col-left">
               	   <h1>{props.heading}</h1>
               	      <h1>{props.subHeading}</h1>
                            <p>{props.text}</p>
                               <a href="#" class="hero-btn red-btn">{props.btnTitle}</a>
               </div>
               <div class="about-col home-col-right" style="background-image: url({props.aboutImg}); background-size: cover;">
               				<img src={props.aboutImg} alt="about"/>
               </div>
            </div>
    	</section>

    );
};




MapTo('spa-react/components/content/about')(About,CustomEditConfig);
