import React, {Component}from 'react';
import {MapTo} from '@adobe/aem-react-editable-components';
import PropTypes from 'prop-types';
import Header from "../Header/Header";


export const CustomEditConfig = {
       emptyLabel: 'Banner',
       isEmpty: function(props) {
         return !props || !props.banner_img;
       }
};

export default function Banner(props){
console.log('Banner props:', props);  // Log props to check their values

    if (CustomEditConfig.isEmpty(props)) {

        return null;
    }
 console.log('Rendering Banner component');
    return (
    	<section className="banner">
    	  <a  href="#"><img src={props.banner_img} alt="Banner Image"/></a>
    		<Header/>

    		<div className="text-box">
    			<h1>{props.title}</h1>
    			<p>{props.text}</p>
    			<a href={props.btn_link} className="hero-btn">{props.btn_title}</a>
    		</div>

    	</section>

    );
};




MapTo('spa-react/components/content/baner')(Banner,CustomEditConfig);
