import React, {Component}from 'react';
import {MapTo} from '@adobe/aem-react-editable-components';
import PropTypes from 'prop-types';
import Header from "../Header/Header";


export const CustomEditConfig = {
       emptyLabel: 'Banner',
       isEmpty: function(props) {
         return !props || !props.baner_img;
       }
};

export default function Banner(props){
    if (CustomEditConfig.isEmpty(props)) {
        return null;
    }

    return (
    	<section className="banner">
    	  <a  href="#"><img src="index.jpg" alt="Banner Image"/></a>
    		<Header/>

    		<div className="text-box">
    			<h1>Infodales Tech solution</h1>
    			<p>hello}</p>
    			<a href="#" className="hero-btn">button</a>
    		</div>

    	</section>

    );
};

Banner.propTypes = {
  baner_img: PropTypes.string,
  title: PropTypes.string,
  text: PropTypes.string,
  btn_link: PropTypes.string,
  btn_title: PropTypes.string
};


MapTo('spa-react/components/content/baner')(Banner,CustomEditConfig);
