import React from 'react';
import { MapTo } from '@adobe/aem-react-editable-components';
import './Footer.css'; // Correct CSS import

export const CustomEditConfig = {
    emptyLabel: 'Footer',
    isEmpty: function(props) {
        return !props || !props.footerHeading;
    }
};

export default function Footer(props) {
    console.log('Props:', props); // Debugging

    if (CustomEditConfig.isEmpty(props)) {
        return <div><h1>Hello</h1></div>;
    }

    // Ensure footerLink is not undefined
    const footerItems = Array.isArray(props.footerLink) ? props.footerLink : [];
    const footerSocialItems = Array.isArray(props.footerSocialIcons) ? props.footerSocialIcons : [];
    console.log('Footer Items:', footerItems); // Debugging
    console.log('Footer Social Items:', footerSocialItems); // Debugging

    return (
        <div>
            {/* Social Icons Section */}
            <div className="social-icons">
                {footerSocialItems.map((item, index) => (
                    <div key={index}>
                        <a href={item.iconUrl} className="linkedin">
                            <img src={item.iconImage} alt="LinkedIn" />
                        </a>
                    </div>
                ))}
            </div>

            {/* Copyright and Footer Menu Section */}
            <div className="copyright-area">
                <div className="footer">
                    <div className="row" style={{ marginTop: '0px' }}>
                        <div className="col-xl-4 col-lg-4 text-center text-lg-left">
                            <div className="copyright-text">
                                <p>
                                    {props.footerHeading}
                                    <a href={`${props.infodalesLink}.html`}>{props.infodales}</a>
                                </p>
                            </div>
                        </div>

                        <div className="menu-flex">
                            {footerItems.map((item, index) => (
                                <div className="footer-menu" style={{ marginTop: '10px' }} key={index}>
                                    <ul>
                                        <li><a href={`${item.footerUrl}.html`}>{item.footerName}</a></li>
                                    </ul>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

// Map the component to AEM
MapTo('spa-react/components/content/footer')(Footer, CustomEditConfig);
