import React, {Component}from 'react';
import {MapTo} from '@adobe/aem-react-editable-components';
import PropTypes from 'prop-types';
import Header from "../Header";


export const CustomEditConfig = {
       emptyLabel: 'Banner',
       isEmpty: function(props) {
         return !props || !props.baner_img;
       }
};

export default function Banner(props){
    if (CustomEditConfig.isEmpty(props)) {
        return null;
    }

    return (
    	<section class="banner">
    	  <a><img src={props.baner_img}/></a>
    		<Header/>

    		<div class="text-box">
    			<h1>{props.title}</h1>
    			<p>{props.text}</p>
    			<a href={props.btn_link} class="hero-btn">{props.btn_title}</a>
    		</div>

    	</section>

    );
};


MapTo('spa-react/components/content/baner')(Banner,CustomEditConfig);
